pragma solidity ^0.8.0;

import "./interfaces/IMarebitsLocker.sol";
import "./interfaces/IMarebitsLockerAccount.sol";
import "./KnowsBestPony.sol";
import "./MarebitsLockerAccount.sol";
import "./MarebitsLockerToken.sol";
import "./MarebitsVault.sol";
import "./RecoverableEther.sol";
import "./RecoverableTokens.sol";
import "./TokenTypeable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Burnable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/introspection/ERC165.sol";

contract MarebitsLocker is ERC165, TokenTypeable, RecoverableEther, RecoverableTokens, KnowsBestPony, ReentrancyGuard, IMarebitsLocker {
	using Address for address;
	using SafeERC20 for IERC20;

	// account handler
	MarebitsLockerAccount private _lockerAccount;

	// issued locker token contract
	MarebitsLockerToken private _lockerToken;

	// vault where all tokens (ERC20 and ERC721) are held in escrow while locked
	MarebitsVault private _vault;

	/**
	 * @dev Only humans can interact
	 */
	modifier onlyHuman() {
		require(!_msgSender().isContract(), "Caller cannot be a smart contract");
		_;
	}

	constructor(string memory name, string memory symbol, string memory baseURI) {
		_lockerAccount = new MarebitsLockerAccount();
		_lockerToken = new MarebitsLockerToken(name, symbol, baseURI);
		_vault = new MarebitsVault();
	}

	function __recoverEther() public override(RecoverableEther) payable onlyOwner {
		_lockerAccount.__recoverEther();
		_lockerToken.__recoverEther();
		_vault.__recoverEther();
		super.__recoverEther();
	}

	function __recoverTokens(TokenType tokenType, address tokenContract, uint256 tokenId) public override(RecoverableTokens) onlyOwner {
		_lockerAccount.__recoverTokens(tokenType, tokenContract, tokenId);
		_lockerToken.__recoverTokens(tokenType, tokenContract, tokenId);
		super.__recoverTokens(tokenType, tokenContract, tokenId);
	}

	function __setBaseURI(string calldata baseURI) external onlyOwner { _lockerToken.__setBaseURI(baseURI); }

	function __setTokenURI(uint256 tokenId, string calldata tokenUri) external onlyOwner { _lockerToken.__setTokenURI(tokenId, tokenUri); }
	
	function _createAccount(uint256 amount, address tokenContract, uint256 tokenId, TokenType tokenType, uint256 unlockTime) private returns (uint256 accountId) {
		accountId = _lockerToken.__getNextTokenId();
		_lockerAccount.__createAccount(accountId, amount, tokenContract, tokenId, tokenType, unlockTime);
	}

	function _lockERC1155(IERC1155 token, uint256 tokenId, uint256 amount, uint256 unlockTime) private returns (uint256 accountId) {
		require(amount > 0, "`amount` must be > 0");
		require(token.balanceOf(_msgSender(), tokenId) >= amount, "`amount` must be <= total token balance");
		require(token.isApprovedForAll(_msgSender(), address(this)), "Not approved, you must call `token.setApprovalForAll()`");
		accountId = _createAccount(amount, address(token), tokenId, TokenType.ERC1155, unlockTime);
		token.safeTransferFrom(_msgSender(), payable(address(_vault)), tokenId, amount, "");
		_lockerToken.__issueToken(payable(_msgSender()), accountId, _lockerAccount.getTokenUri(accountId));
		emit TokensLocked(accountId, _msgSender(), amount, address(token), tokenId, TokenType.ERC1155, unlockTime);
	}

	function _lockERC20(IERC20 token, uint256 amount, uint256 unlockTime) private returns (uint256 accountId) {
		require(amount > 0, "`amount` must be > 0");
		require(token.balanceOf(_msgSender()) >= amount, "`amount` must be <= total token balance");
		require(token.allowance(_msgSender(), address(this)) >= amount, "Not approved, you must call `token.approve()`");
		accountId = _createAccount(amount, address(token), 0, TokenType.ERC20, unlockTime);
		token.safeTransferFrom(_msgSender(), payable(address(_vault)), amount);
		_lockerToken.__issueToken(payable(_msgSender()), accountId, _lockerAccount.getTokenUri(accountId));
		emit TokensLocked(accountId, _msgSender(), amount, address(token), 0, TokenType.ERC20, unlockTime);
	}

	function _lockERC721(IERC721 token, uint256 tokenId, uint256 unlockTime) private returns (uint256 accountId) {
		require(token.ownerOf(tokenId) == _msgSender(), "`tokenId` not owned by caller");
		require(token.getApproved(tokenId) == address(this), "Not approved, you must call `token.approve()`");
		accountId = _createAccount(1, address(token), tokenId, TokenType.ERC721, unlockTime);
		token.safeTransferFrom(_msgSender(), payable(address(_vault)), tokenId);
		_lockerToken.__issueToken(payable(_msgSender()), accountId, _lockerAccount.getTokenUri(accountId));
		emit TokensLocked(accountId, _msgSender(), 1, address(token), tokenId, TokenType.ERC721, unlockTime);
	}

	function extendLock(uint256 accountId, uint256 unlockTime) external onlyHuman {
		require(_lockerToken.__exists(accountId), "Token for `accountId` does not exist");
		require(_lockerToken.ownerOf(accountId) == _msgSender(), "Not the owner of this `accountId`");
		require(unlockTime > _lockerAccount.getUnlockTime(accountId), "New `unlockTime` must be > the existing `unlockTime`");
		_lockerAccount.__setUnlockTime(accountId, unlockTime);
		emit TokensLocked(accountId, _msgSender(), _lockerAccount.getAmount(accountId), _lockerAccount.getTokenContract(accountId), _lockerAccount.getTokenId(accountId), _lockerAccount.getTokenType(accountId), unlockTime);
	}

	function getAccount(uint256 accountId) external view returns (IMarebitsLockerAccount.Account memory) { return _lockerAccount.getAccount(accountId); }

	function lockTokens(TokenType tokenType, address tokenContract, uint256 tokenId, uint256 amount, uint256 unlockTime) external nonReentrant onlyHuman isValidTokenType(tokenType) returns (uint256) {
		require(unlockTime > block.timestamp, "`unlockTime` must be in the future");

		if (tokenType == TokenType.ERC1155) {
			return _lockERC1155(IERC1155(tokenContract), tokenId, amount, unlockTime);
		} else if (tokenType == TokenType.ERC20) {
			return _lockERC20(IERC20(tokenContract), amount, unlockTime);
		} else if (tokenType == TokenType.ERC721) {
			return _lockERC721(IERC721(tokenContract), tokenId, unlockTime);
		} else {
			return 0;
		}
	}

	function redeemToken(uint256 accountId) external nonReentrant onlyHuman {
		require(_lockerToken.__exists(accountId), "Token for `accountId` does not exist");
		require(_lockerToken.ownerOf(accountId) == _msgSender(), "Not the owner of this `accountId`");
		require(_lockerToken.getApproved(accountId) == address(this), "Not approved, you must call `token.approve()`");
		require(_lockerAccount.isUnlocked(accountId), "`unlockTime` not expired");
		require(_lockerAccount.getAmount(accountId) > 0, "Account has no balance");
		_lockerAccount.__setAmount(accountId, 0);
		_lockerToken.__burn(accountId);
		_vault.__transfer(_lockerAccount.getTokenType(accountId), _lockerAccount.getTokenContract(accountId), payable(_msgSender()), _lockerAccount.getTokenId(accountId), _lockerAccount.getAmount(accountId));
		emit TokensRedeemed(accountId, _msgSender(), _lockerAccount.getAmount(accountId), _lockerAccount.getTokenContract(accountId), _lockerAccount.getTokenId(accountId), _lockerAccount.getTokenType(accountId));
	}

	/**
	* @dev Implementation of the {IERC165} interface.
	* @param interfaceId to check
	* @return true if the interfaceId is supported and false if it is not
	*/
	function supportsInterface(bytes4 interfaceId) public view virtual override(IERC165, ERC165) returns (bool) {
		return interfaceId == type(KnowsBestPony).interfaceId || 
			interfaceId == type(Ownable).interfaceId || 
			interfaceId == type(RecoverableEther).interfaceId ||
			interfaceId == type(RecoverableTokens).interfaceId || 
			interfaceId == type(IMarebitsLocker).interfaceId || 
			super.supportsInterface(interfaceId);
	}
}","keccak256":"0x5cfb2f256989de431082c3a3cac1b7b1246039fa080010ec8c8b403b851a0a6b","license":"LicenseRef-DSPL"},"contracts/MarebitsLockerAccount.sol":{"content":"// SPDX-License-Identifier: LicenseRef-DSPL
pragma solidity ^0.8.0;

import "./interfaces/IMarebitsLockerAccount.sol";
import "./KnowsBestPony.sol";
import "./RecoverableEther.sol";
import "./RecoverableTokens.sol";
import "./TokenTypeable.sol";
import "./verifyIPFS-master/contracts/verifyIPFS.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/introspection/ERC165.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract MarebitsLockerAccount is ERC165, TokenTypeable, RecoverableEther, RecoverableTokens, KnowsBestPony, IMarebitsLockerAccount {
	using Strings for uint256;

	mapping(uint256 => Account) private _accounts;

	function __createAccount(uint256 accountId, uint256 amount, address tokenContract, uint256 tokenId, TokenType tokenType, uint256 unlockTime) public onlyOwner {
		require(amount > 0, "`amount` must be greater than 0");
		require(tokenType != TokenType.ERC721 || amount == 1, "`amount` must be 1 for ERC721 tokens");
		require(tokenType == TokenType.ERC20 || tokenId > 0, "`tokenId` must be specified for ERC721 or ERC1155 tokens");
		require(unlockTime > block.timestamp, "`unlockTime` must be in the future");
		Account storage account = _accounts[accountId];
		(account.amount, account.tokenContract, account.tokenId, account.tokenType, account.unlockTime) = (amount, tokenContract, tokenId, tokenType, unlockTime);
		_generateMetadata(account);
	}

	function __setAmount(uint256 accountId, uint256 amount) external onlyOwner {
		_accounts[accountId].amount = amount;
		_generateMetadata(_accounts[accountId]);
	}

	function __setUnlockTime(uint256 accountId, uint256 unlockTime) external onlyOwner {
		_accounts[accountId].unlockTime = unlockTime;
		_generateMetadata(_accounts[accountId]);
	}

	function _generateMetadata(Account storage account) private {
		account.metadata = string(abi.encodePacked('{"description":"This token entitles the holder to withdraw their locked token(s) after their lock period expires from the Mare Bits Locker.","external_url":"https://mare.biz/","image":"https://mare.biz/marebits/icon-960.png","name":"\\uD83D\\uDC0E\\u200D\\u2640\\uFE0F\\uD83D\\uDD12\\uD83E\\uDE99","properties": {"amount":"', account.amount.toString(), '","tokenContract":"', uint256(uint160(account.tokenContract)).toString(), '","tokenType":"', (account.tokenType == TokenType.ERC1155) ? "ERC1155" : (account.tokenType == TokenType.ERC20) ? "ERC20" : (account.tokenType == TokenType.ERC721) ? "ERC721" : "UNKNOWN", '",', (account.tokenId > 0) ? string(abi.encodePacked('"tokenId":"', account.tokenId.toString(), '",')) : "", '"unlockTime":"', account.unlockTime.toString(), '"}}'));
		account.tokenUri = string(verifyIPFS.generateHash(account.metadata));
	}

	function getAccount(uint256 accountId) external view returns (Account memory) { return _accounts[accountId]; }

	function getAmount(uint256 accountId) external view returns (uint256) { return _accounts[accountId].amount; }

	function getMetadata(uint256 accountId) external view returns (string memory) { return _accounts[accountId].metadata; }

	function getTokenContract(uint256 accountId) external view returns (address) { return _accounts[accountId].tokenContract; }

	function getTokenType(uint256 accountId) external view returns (TokenType) { return _accounts[accountId].tokenType; }

	function getTokenId(uint256 accountId) external view returns (uint256) { return _accounts[accountId].tokenId; }

	function getTokenUri(uint256 accountId) external view returns (string memory) { return _accounts[accountId].tokenUri; }

	function getUnlockTime(uint256 accountId) external view returns (uint256) { return _accounts[accountId].unlockTime; }

	function isUnlocked(uint256 accountId) external view returns (bool) { return _accounts[accountId].unlockTime <= block.timestamp; }

	/**
	* @dev Implementation of the {IERC165} interface.
	* @param interfaceId to check
	* @return true if the interfaceId is supported and false if it is not
	*/
	function supportsInterface(bytes4 interfaceId) public view virtual override(IERC165, ERC165) returns (bool) {
		return interfaceId == type(KnowsBestPony).interfaceId || 
			interfaceId == type(Ownable).interfaceId || 
			interfaceId == type(RecoverableEther).interfaceId ||
			interfaceId == type(RecoverableTokens).interfaceId || 
			interfaceId == type(IMarebitsLockerAccount).interfaceId || 
			super.supportsInterface(interfaceId);
	}
}","keccak256":"0x8055423ca9178694c5cbaf302ac057f0478d5d6bcd9a937b36d688e096fea365","license":"LicenseRef-DSPL"},"contracts/MarebitsLockerToken.sol":{"content":"// SPDX-License-Identifier: LicenseRef-DSPL
pragma solidity ^0.8.0;

import "./interfaces/IMarebitsLockerToken.sol";
import "./KnowsBestPony.sol";
import "./RecoverableEther.sol";
import "./RecoverableTokens.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Burnable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

contract MarebitsLockerToken is RecoverableEther, RecoverableTokens, ERC721URIStorage, KnowsBestPony, IMarebitsLockerToken {
	using Counters for Counters.Counter;

	string private __baseURI;

	// count tokens as they're created
	Counters.Counter private _tokenIdTracker;

	constructor(string memory name, string memory symbol, string memory baseURI) ERC721(name, symbol) { __baseURI = baseURI; }

	function __burn(uint256 tokenId) external onlyOwner { _burn(tokenId); }

	function __exists(uint256 tokenId) external view onlyOwner returns (bool) { return _exists(tokenId); }

	function __getNextTokenId() external onlyOwner returns (uint256 tokenId) {
		tokenId = _tokenIdTracker.current();
		_tokenIdTracker.increment();
	}

	function __issueToken(address payable owner, uint256 tokenId, string memory tokenUri) external onlyOwner {
		_safeMint(owner, tokenId);
		_setTokenURI(tokenId, tokenUri);
	}

	function __setBaseURI(string calldata baseURI) external onlyOwner { __baseURI = baseURI; }

	function __setTokenURI(uint256 tokenId, string calldata tokenUri) external onlyOwner { _setTokenURI(tokenId, tokenUri); }

	function _baseURI() internal view override returns (string memory) { return __baseURI; }

	/**
	* @dev Burns `tokenId`. See {ERC721-_burn}.
	*/
	function burn(uint256 tokenId) external {
		require(_isApprovedOrOwner(_msgSender(), tokenId), "Not approved or owner");
		_burn(tokenId);
	}

	/**
	* @dev Implementation of the {IERC165} interface.
	* @param interfaceId to check
	* @return true if the interfaceId is supported and false if it is not
	*/
	function supportsInterface(bytes4 interfaceId) public view virtual override(IERC165, ERC721) returns (bool) {
		return interfaceId == type(KnowsBestPony).interfaceId || 
			interfaceId == type(Ownable).interfaceId || 
			interfaceId == type(RecoverableEther).interfaceId ||
			interfaceId == type(RecoverableTokens).interfaceId || 
			interfaceId == type(ERC721URIStorage).interfaceId || 
			interfaceId == type(IMarebitsLockerToken).interfaceId || 
			ERC721.supportsInterface(interfaceId);
	}
}","keccak256":"0x566afe111daf0f65683de0419f08f98e155d1cf3c47b5d209fccc0bbd36c88e8","license":"LicenseRef-DSPL"},"contracts/MarebitsVault.sol":{"content":"// SPDX-License-Identifier: LicenseRef-DSPL
pragma solidity ^0.8.0;

import "./KnowsBestPony.sol";
import "./RecoverableEther.sol";
import "./TokenTypeable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Receiver.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";

contract MarebitsVault is ERC1155Holder, ERC721Holder, KnowsBestPony, RecoverableEther, TokenTypeable {
	using SafeERC20 for IERC20;

	function __transfer(TokenType tokenType, address tokenContract, address payable to, uint256 tokenId, uint256 amount) external onlyOwner {
		if (tokenType == TokenType.ERC1155) {
			_transferERC1155(IERC1155(tokenContract), to, tokenId, amount);
		} else if (tokenType == TokenType.ERC20) {
			_transferERC20(IERC20(tokenContract), to, amount);
		} else if (tokenType == TokenType.ERC721) {
			_transferERC721(IERC721(tokenContract), to, tokenId);
		}
	}

	/**
	 * @dev Transfer ERC1155 tokens to destination, used to move tokens from the vault to the owner on withdraw
	 * @param token to transfer
	 * @param to wallet address
	 * @param amount of ERC1155 tokens to transfer
	 */
	function _transferERC1155(IERC1155 token, address payable to, uint256 tokenId, uint256 amount) private { token.safeTransferFrom(address(this), to, tokenId, amount, ""); }

	/**
	 * @dev Transfer ERC20 tokens to destination, used to move tokens from the vault to the owner on withdraw
	 * @param token to transfer
	 * @param to wallet address
	 * @param amount of ERC20 tokens to transfer
	 */
	function _transferERC20(IERC20 token, address payable to, uint256 amount) private { token.safeTransfer(to, amount); }

	/**
	 * @dev Transfer ERC721 token to destination, used to move token from the vault to the owner on withdraw
	 * @param token to transfer
	 * @param to wallet address
	 * @param tokenId of the ERC721 token to transfer
	 */
	function _transferERC721(IERC721 token, address payable to, uint256 tokenId) private { token.safeTransferFrom(address(this), to, tokenId); }

	/**
	* @dev Implementation of the {IERC165} interface.
	* @param interfaceId to check
	* @return true if the interfaceId is supported and false if it is not
	*/
	function supportsInterface(bytes4 interfaceId) public view virtual override(ERC1155Receiver) returns (bool) {
		return interfaceId == type(IERC721Receiver).interfaceId || 
			interfaceId == type(KnowsBestPony).interfaceId || 
			interfaceId == type(Ownable).interfaceId || 
			interfaceId == type(RecoverableEther).interfaceId || 
			super.supportsInterface(interfaceId);
	}
}","keccak256":"0x5ae8351c46716cc35168085a2bd372119079ee544fabb89186eac65224e75046","license":"LicenseRef-DSPL"},"contracts/RecoverableEther.sol":{"content":"// SPDX-License-Identifier: LicenseRef-DSPL
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";

abstract contract RecoverableEther is Ownable {
	/**
	 * @dev Redirect sent ether, no ether should ever be sent to this contract
	 */
	modifier redirectEther() {
		if (msg.value > 0) {
			payable(owner()).transfer(msg.value);
		}
		_;
	}
	
	function __recoverEther() public virtual payable onlyOwner { payable(owner()).transfer(address(this).balance); }
}","keccak256":"0x80dd686ff57c0bd1e6fa13954e7dffd87d2e2ec4076d0d2864d228fb1af52a4d","license":"LicenseRef-DSPL"},"contracts/RecoverableTokens.sol":{"content":"// SPDX-License-Identifier: LicenseRef-DSPL
pragma solidity ^0.8.0;

import "./TokenTypeable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

abstract contract RecoverableTokens is Ownable, TokenTypeable {
	using SafeERC20 for IERC20;

	/**
	 * @dev Withdraw the ERC1155 token of the given type to this contract owner's wallet, does not effect the vault. No tokens should be sent directly to this contract, this fixes instances where they mistakenly are.
	 * @param token to withdraw
	 * @param tokenId to withdraw
	 */
	function __recoverERC1155(IERC1155 token, uint256 tokenId) internal { token.safeTransferFrom(address(this), payable(owner()), tokenId, token.balanceOf(address(this), tokenId), ""); }

	/**
	 * @dev Withdraw all ERC20 tokens of the given type to this contract owner's wallet, does not effect the vault. No tokens should be sent directly to this contract, this fixes instances where they mistakenly are.
	 * @param token to withdraw
	 */
	function __recoverERC20(IERC20 token) internal { token.safeTransfer(payable(owner()), token.balanceOf(address(this))); }

	/**
	 * @dev Withdraw the ERC721 token of the given type to this contract owner's wallet, does not effect the vault. No tokens should be sent directly to this contract, this fixes instances where they mistakenly are.
	 * @param token to withdraw
	 * @param tokenId to withdraw
	 */
	function __recoverERC721(IERC721 token, uint256 tokenId) internal { token.safeTransferFrom(address(this), payable(owner()), tokenId); }

	function __recoverTokens(TokenType tokenType, address tokenContract, uint256 tokenId) public virtual onlyOwner {
		if (tokenType == TokenType.ERC1155) {
			__recoverERC1155(IERC1155(tokenContract), tokenId);
		} else if (tokenType == TokenType.ERC20) {
			__recoverERC20(IERC20(tokenContract));
		} else if (tokenType == TokenType.ERC721) {
			__recoverERC721(IERC721(tokenContract), tokenId);
		}
	}
}","keccak256":"0x2551b6f56d046ce1948ade716ca5a820c49823d4425e92444b5ba2946a66b94f","license":"LicenseRef-DSPL"},"contracts/TokenTypeable.sol":{"content":"// SPDX-License-Identifier: LicenseRef-DSPL
pragma solidity ^0.8.0;

import "./interfaces/ITokenTypeable.sol";

abstract contract TokenTypeable is ITokenTypeable {
	modifier isValidTokenType(TokenType tokenType) {
		require(tokenType == TokenType.ERC1155 || tokenType == TokenType.ERC20 || tokenType == TokenType.ERC721, "`tokenType` must represent a supported token: 1 for ERC1155, 2 for ERC20, or 3 for ERC721");
		_;
	}
}","keccak256":"0x38c13c68c0fcb689610509566fc2529f29a985f376d4654ab0304f1b22c9b996","license":"LicenseRef-DSPL"},"contracts/interfaces/IMarebitsLocker.sol":{"content":"// SPDX-License-Identifier: LicenseRef-DSPL
